import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { FiArrowLeft, FiPlay, FiPause, FiVolume2, FiVolumeX, FiMaximize, FiSettings, FiRotateCcw, FiAlertTriangle, FiCheckCircle, FiLoader } from 'react-icons/fi';
import { moviesApi, tvApi } from '../../services/tmdbApi';
import { vidsrcApi, createEmbedCode } from '../../services/vidsrcApi';
import { useSettings } from '../../context/SettingsContext';
import { getImageUrl } from '../../services/tmdbApi';
import './Player.css';

const Player = () => {
  const { type, id } = useParams();
  const navigate = useNavigate();
  const { settings } = useSettings();
  const iframeRef = useRef(null);
  const controlsTimeoutRef = useRef(null);

  // Enhanced state management
  const [content, setContent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [loadingPhase, setLoadingPhase] = useState('initializing');
  const [error, setError] = useState(null);
  const [streamData, setStreamData] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [isMuted, setIsMuted] = useState(false);
  const [showControls, setShowControls] = useState(true);
  const [quality, setQuality] = useState('auto');
  const [availableQualities, setAvailableQualities] = useState([]);
  const [showQualitySelector, setShowQualitySelector] = useState(false);
  const [retryCount, setRetryCount] = useState(0);
  const [fallbackStatus, setFallbackStatus] = useState(null);
  const [performanceMetrics, setPerformanceMetrics] = useState(null);
  const [isMobile, setIsMobile] = useState(false);
  const [touchControls, setTouchControls] = useState({
    brightness: 50,
    volume: 50,
    seeking: false
  });

  useEffect(() => {
    loadContent();
    detectMobileDevice();

    // Reset fallback manager when component mounts
    vidsrcApi.resetFallbackManager();

    return () => {
      if (controlsTimeoutRef.current) {
        clearTimeout(controlsTimeoutRef.current);
      }
    };
  }, [type, id]);

  useEffect(() => {
    if (content) {
      loadStream();
    }
  }, [content, quality]);

  // Detect mobile device
  const detectMobileDevice = () => {
    const mobile = /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent) ||
                   window.innerWidth <= 768;
    setIsMobile(mobile);
  };

  const loadContent = async () => {
    try {
      setLoading(true);
      setLoadingPhase('initializing');
      setError(null);

      let contentData;
      if (type === 'movie') {
        setLoadingPhase('fetching_metadata');
        contentData = await moviesApi.getDetails(id);
      } else {
        setLoadingPhase('fetching_metadata');
        contentData = await tvApi.getDetails(id);
      }

      setContent(contentData);
      setAvailableQualities(vidsrcApi.getQualityOptions(type));
    } catch (err) {
      console.error('Error loading content:', err);
      setError('Failed to load content details');
    } finally {
      setLoading(false);
    }
  };

  const loadStream = async () => {
    try {
      setLoadingPhase('loading_stream');
      setError(null);

      let stream;
      if (type === 'movie') {
        stream = vidsrcApi.getMovieStream(id, { quality });
      } else {
        // For TV shows, we'd need season/episode info
        // For now, default to season 1 episode 1
        stream = vidsrcApi.getTVStream(id, 1, 1, { quality });
      }

      setStreamData(stream);
      setLoadingPhase('preparing_player');

      // Test stream availability
      const testResult = await vidsrcApi.testStream(stream.url);
      if (!testResult.available) {
        throw new Error(`Stream not available: ${testResult.error}`);
      }

      setLoadingPhase('ready');
      setFallbackStatus(vidsrcApi.getFallbackStatus());
    } catch (err) {
      console.error('Error loading stream:', err);
      await handleStreamError(err);
    }
  };

  const handleStreamError = async (error) => {
    const context = {
      type,
      id,
      quality,
      retryCount
    };

    try {
      const recoveryResult = await vidsrcApi.handleStreamError(error, context);

      if (recoveryResult.success) {
        setStreamData(prev => ({ ...prev, url: recoveryResult.url }));
        setRetryCount(prev => prev + 1);
        setFallbackStatus(vidsrcApi.getFallbackStatus());
      } else {
        setError(`Streaming failed: ${recoveryResult.error}`);
      }
    } catch (recoveryError) {
      setError(`All streaming sources failed. Please try again later.`);
    }
  };

  const handlePlayPause = () => {
    setIsPlaying(!isPlaying);
  };

  const handleMuteToggle = () => {
    setIsMuted(!isMuted);
  };

  const handleMouseMove = () => {
    setShowControls(true);

    if (controlsTimeout) {
      clearTimeout(controlsTimeout);
    }

    const timeout = setTimeout(() => {
      setShowControls(false);
    }, 3000);

    setControlsTimeout(timeout);
  };

  const handleFullscreen = () => {
    if (document.fullscreenElement) {
      document.exitFullscreen();
    } else {
      document.documentElement.requestFullscreen();
    }
  };

  if (loading) {
    return (
      <div className="player-loading">
        <div className="loading-spinner"></div>
        <p>Loading content...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="player-error">
        <div className="error-content">
          <h2>Unable to Load Player</h2>
          <p>{error}</p>
          <Link to="/" className="back-btn">
            <FiArrowLeft />
            Back to Home
          </Link>
        </div>
      </div>
    );
  }

  if (!content || !streamData) {
    return (
      <div className="player-error">
        <div className="error-content">
          <h2>Content Not Found</h2>
          <p>The requested content could not be found.</p>
          <Link to="/" className="back-btn">
            <FiArrowLeft />
            Back to Home
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="player-container" onMouseMove={handleMouseMove}>
      {/* Player Header */}
      <div className={`player-header ${showControls ? 'visible' : 'hidden'}`}>
        <div className="header-left">
          <button
            className="back-btn"
            onClick={() => navigate(-1)}
            aria-label="Go back"
          >
            <FiArrowLeft />
          </button>
          <div className="content-info">
            <h1 className="content-title">{content.title}</h1>
            {content.releaseDate && (
              <span className="content-year">
                {new Date(content.releaseDate).getFullYear()}
              </span>
            )}
          </div>
        </div>

        <div className="header-right">
          <button className="settings-btn" aria-label="Settings">
            <FiSettings />
          </button>
        </div>
      </div>

      {/* Video Player */}
      <div className="video-wrapper">
        <iframe
          src={streamData.url}
          title={`${content.title} - Player`}
          frameBorder="0"
          allowFullScreen
          allow="autoplay; encrypted-media; fullscreen; picture-in-picture"
          sandbox="allow-same-origin allow-scripts allow-presentation"
          className="video-player"
        />

        {/* Video Controls Overlay */}
        <div className={`video-controls ${showControls ? 'visible' : 'hidden'}`}>
          <div className="controls-center">
            <button
              className="control-btn play-pause"
              onClick={handlePlayPause}
              aria-label={isPlaying ? 'Pause' : 'Play'}
            >
              {isPlaying ? <FiPause /> : <FiPlay />}
            </button>
          </div>

          <div className="controls-bottom">
            <div className="controls-left">
              <button
                className="control-btn"
                onClick={handleMuteToggle}
                aria-label={isMuted ? 'Unmute' : 'Mute'}
              >
                {isMuted ? <FiVolumeX /> : <FiVolume2 />}
              </button>
            </div>

            <div className="controls-right">
              <button
                className="control-btn fullscreen"
                onClick={handleFullscreen}
                aria-label="Toggle fullscreen"
              >
                <FiMaximize />
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Content Info Panel */}
      <div className="content-panel">
        <div className="panel-poster">
          <img
            src={getImageUrl(content.posterPath)}
            alt={content.title}
            loading="lazy"
          />
        </div>

        <div className="panel-info">
          <h2 className="panel-title">{content.title}</h2>

          <div className="panel-meta">
            {content.voteAverage > 0 && (
              <div className="panel-rating">
                <span className="rating-star">‚≠ê</span>
                <span>{content.voteAverage.toFixed(1)}</span>
              </div>
            )}
            {content.releaseDate && (
              <span className="panel-year">
                {new Date(content.releaseDate).getFullYear()}
              </span>
            )}
            {content.runtime && (
              <span className="panel-runtime">
                {content.runtime} min
              </span>
            )}
          </div>

          {content.overview && (
            <p className="panel-description">
              {content.overview}
            </p>
          )}

          <div className="panel-actions">
            <Link
              to={`/${type}/${id}`}
              className="more-info-btn"
            >
              More Information
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Player;
