// VidSrc API service for streaming integration
// No API key required - uses TMDB IDs directly

// Simplified domain configuration - vidsrc.net as primary source
const VIDSRC_DOMAINS = {
  primary: 'https://vidsrc.net/embed',
  fallbacks: [
    'https://vidsrc.to/embed',
    'https://vidsrc.cc/embed'
  ]
};

// Quality options for streaming
const QualityOptions = {
  AUTO: 'auto',
  Q4K: '4k',
  Q1080P: '1080p',
  Q720P: '720p',
  Q480P: '480p',
  Q360P: '360p'
};

// Error types for better error handling
const StreamErrorTypes = {
  NETWORK_ERROR: 'NETWORK_ERROR',
  TIMEOUT_ERROR: 'TIMEOUT_ERROR',
  PLAYER_ERROR: 'PLAYER_ERROR',
  CONTENT_NOT_FOUND: 'CONTENT_NOT_FOUND',
  GEO_BLOCKED: 'GEO_BLOCKED',
  QUALITY_UNAVAILABLE: 'QUALITY_UNAVAILABLE'
};





// Global instances
const fallbackManager = new FallbackManager();
const errorRecoveryManager = new ErrorRecoveryManager(fallbackManager);
const performanceMonitor = new PerformanceMonitor();

export const vidsrcApi = {
  // Enhanced streaming URL generation with fallback support
  getMovieStream: (tmdbId, options = {}) => {
    const { quality = 'auto', useFallback = true } = options;

    try {
      const url = useFallback
        ? fallbackManager.getNextUrl('movie', tmdbId, null, null, quality)
        : `${VIDSRC_DOMAINS.primary}/movie/${tmdbId}${quality !== 'auto' ? `?q=${quality}` : ''}`;

      return {
        url,
        type: 'movie',
        provider: 'vidsrc',
        embeddable: true,
        quality,
        fallbackSupported: useFallback
      };
    } catch (error) {
      return this.getErrorStream('movie', tmdbId, error);
    }
  },

  // Enhanced TV streaming with fallback support
  getTVStream: (tmdbId, season = 1, episode = 1, options = {}) => {
    const { quality = 'auto', useFallback = true } = options;

    try {
      const url = useFallback
        ? fallbackManager.getNextUrl('tv', tmdbId, season, episode, quality)
        : `${VIDSRC_DOMAINS.primary}/tv/${tmdbId}/${season}/${episode}${quality !== 'auto' ? `?q=${quality}` : ''}`;

      return {
        url,
        type: 'tv',
        provider: 'vidsrc',
        embeddable: true,
        season,
        episode,
        quality,
        fallbackSupported: useFallback
      };
    } catch (error) {
      return this.getErrorStream('tv', tmdbId, error, season, episode);
    }
  },

  // Get error stream for failed requests
  getErrorStream: (type, id, error, season = null, episode = null) => {
    return {
      url: '',
      type,
      provider: 'error',
      embeddable: false,
      error: error.message,
      season,
      episode
    };
  },

  // Try alternative URL patterns for better compatibility (November 2025)
  tryAlternativeUrls: (tmdbId, type, season = null, episode = null) => {
    const alternatives = [];

    // Try current working VidSrc domains from research
    const domains = [
      'https://vidsrc.to/embed',
      'https://vidsrc.cc/embed',
      'https://vidsrc.org/embed',
      'https://vidsrc.me/embed',
      'https://vidsrc.net/embed',
      'https://vidsrc.xyz/embed',
      'https://vidsrc.io/embed',
      'https://vidsrc.vc/embed',
      'https://vidsrc.bz/embed',
      'https://vidsrc.gd/embed'
    ];

    domains.forEach(domain => {
      if (type === 'movie') {
        alternatives.push(`${domain}/movie/${tmdbId}`);
      } else {
        alternatives.push(`${domain}/tv/${tmdbId}/${season || 1}/${episode || 1}`);
      }
    });

    // Add alternative streaming sources
    const altSources = [
      `https://embedsu.com/embed/${tmdbId}`,
      `https://www.2embed.cc/embed/${tmdbId}`,
      `https://embed.su/embed/movie/${tmdbId}`,
      `https://autoembed.cc/embed/${tmdbId}`,
      `https://vidsrcsu.com/embed/${tmdbId}`,
      `https://vidsrc.pk/embed/${tmdbId}`,
      `https://www.embedsoap.com/embed/movie/?id=${tmdbId}`,
      `https://player.smashystream.com/movie/${tmdbId}`,
      `https://multiembed.mov/directstream.php?video_id=${tmdbId}`,
      `https://vidplay.online/embed/${tmdbId}`,
      `https://www.solarmovie.pe/embed/${tmdbId}`,
      `https://flixhq.to/embed/${tmdbId}`
    ];

    alternatives.push(...altSources);

    return alternatives;
  },

  // Enhanced multiple streaming sources with quality options
  getMovieSources: (tmdbId, options = {}) => {
    const { includeAlternatives = true, quality = 'auto' } = options;
    const sources = [];

    // Primary VidSrc sources with different qualities
    const qualities = [QualityOptions.Q4K, QualityOptions.Q1080P, QualityOptions.Q720P, QualityOptions.Q480P];

    qualities.forEach((q, index) => {
      sources.push({
        id: `vidsrc-${q}`,
        name: `VidSrc ${q.toUpperCase()}`,
        url: fallbackManager.getNextUrl('movie', tmdbId, null, null, q),
        embeddable: true,
        quality: q,
        provider: 'vidsrc',
        primary: index === 0
      });
    });

    // Backup domains for primary quality
    if (includeAlternatives) {
      VIDSRC_DOMAINS.fallbacks.forEach((domain, index) => {
        sources.push({
          id: `vidsrc-backup-${index}`,
          name: `VidSrc Backup ${index + 1}`,
          url: `${domain}/movie/${tmdbId}${quality !== 'auto' ? `?q=${quality}` : ''}`,
          embeddable: true,
          quality: quality,
          provider: 'vidsrc',
          backup: true
        });
      });

      // Alternative sources - using the fallback manager for correct URL patterns
      VIDSRC_DOMAINS.alternative.forEach((domain, index) => {
        const sourceId = `alt-${index}`;
        let sourceName = `Alternative ${index + 1}`;
        let provider = `alternative-${index}`;

        // Set meaningful names for known sources
        if (domain.includes('2embed.cc')) {
          sourceName = '2Embed';
          provider = '2embed';
        } else if (domain.includes('embedsu.com')) {
          sourceName = 'EmbedSu';
          provider = 'embedsu';
        } else if (domain.includes('autoembed.cc')) {
          sourceName = 'AutoEmbed';
          provider = 'autoembed';
        } else if (domain.includes('embed.su')) {
          sourceName = 'Embed.su';
          provider = 'embedsu';
        } else if (domain.includes('smashystream.com')) {
          sourceName = 'SmashyStream';
          provider = 'smashystream';
        } else if (domain.includes('multiembed.mov')) {
          sourceName = 'MultiEmbed';
          provider = 'multiembed';
        } else if (domain.includes('vidplay.online')) {
          sourceName = 'VidPlay';
          provider = 'vidplay';
        } else if (domain.includes('embedsoap.com')) {
          sourceName = 'EmbedSoap';
          provider = 'embedsoap';
        } else if (domain.includes('solarmovie.pe')) {
          sourceName = 'SolarMovie';
          provider = 'solarmovie';
        } else if (domain.includes('flixhq.to')) {
          sourceName = 'FlixHQ';
          provider = 'flixhq';
        } else if (domain.includes('multiembed.mov')) {
          sourceName = 'MultiEmbed';
          provider = 'multiembed';
        } else if (domain.includes('player.smashystream.com')) {
          sourceName = 'SmashyStream';
          provider = 'smashystream';
        }

        sources.push({
          id: sourceId,
          name: sourceName,
          url: fallbackManager.getNextUrl('movie', tmdbId, null, null, quality),
          embeddable: true,
          quality: 'HD',
          provider: provider,
          alternative: true
        });
      });
    }

    return sources;
  },

  // Enhanced TV sources with quality options
  getTVSources: (tmdbId, season = 1, episode = 1, options = {}) => {
    const { includeAlternatives = true, quality = 'auto' } = options;
    const sources = [];

    // Primary VidSrc sources with different qualities
    const qualities = [QualityOptions.Q1080P, QualityOptions.Q720P, QualityOptions.Q480P];

    qualities.forEach((q, index) => {
      sources.push({
        id: `vidsrc-${q}`,
        name: `VidSrc ${q.toUpperCase()}`,
        url: fallbackManager.getNextUrl('tv', tmdbId, season, episode, q),
        embeddable: true,
        quality: q,
        provider: 'vidsrc',
        season,
        episode,
        primary: index === 0
      });
    });

    // Backup domains for primary quality
    if (includeAlternatives) {
      VIDSRC_DOMAINS.fallbacks.forEach((domain, index) => {
        sources.push({
          id: `vidsrc-backup-${index}`,
          name: `VidSrc Backup ${index + 1}`,
          url: `${domain}/tv/${tmdbId}/${season}/${episode}${quality !== 'auto' ? `?q=${quality}` : ''}`,
          embeddable: true,
          quality: quality,
          provider: 'vidsrc',
          season,
          episode,
          backup: true
        });
      });
    }

    return sources;
  },

  // Check if streaming is available for a movie
  checkAvailability: async (tmdbId, type = 'movie') => {
    try {
      // VidSrc doesn't have a formal availability check API
      // We assume content is available if it has a valid TMDB ID
      return {
        available: true,
        provider: 'vidsrc',
        embeddable: true,
        message: 'Streaming available via VidSrc'
      };
    } catch (error) {
      return {
        available: false,
        provider: 'vidsrc',
        embeddable: false,
        message: 'Streaming temporarily unavailable'
      };
    }
  },

  // Get streaming quality information
  getQualityInfo: () => {
    return {
      default: '1080p',
      available: ['720p', '1080p', '4K'],
      note: 'Quality automatically adjusts based on connection speed'
    };
  },

  // Get supported regions
  getSupportedRegions: () => {
    return {
      regions: ['IN', 'US', 'UK', 'CA', 'AU', 'DE', 'FR', 'JP', 'KR', 'BR', 'MX', 'ES', 'IT', 'NL', 'SE', 'NO', 'DK', 'FI', 'PL', 'RU', 'CN', 'HK', 'TW', 'SG', 'MY', 'TH', 'VN', 'PH', 'ID', 'AE', 'SA', 'EG', 'ZA', 'NG', 'KE', 'MA', 'TN', 'DZ', 'AR', 'CL', 'CO', 'PE', 'VE', 'EC', 'UY', 'PY', 'BO', 'GT', 'CR', 'PA', 'DO', 'JM', 'TT', 'BB', 'LC', 'VC', 'GD', 'AG', 'DM', 'KN', 'MS', 'VG', 'AI', 'BM', 'KY', 'TC', 'FK', 'GS', 'SH', 'IO', 'AC', 'TA', 'PN', 'WF', 'PF', 'NC', 'VU', 'FJ', 'PG', 'SB', 'KI', 'NR', 'TV', 'TO', 'WS', 'CK', 'NU', 'TK', 'AS', 'GU', 'MP', 'FM', 'MH', 'PW', 'UM', 'VI', 'PR'],
      note: 'VidSrc works globally with no regional restrictions'
    };
  },

  // Enhanced error handling with recovery
  handleStreamError: async (error, context) => {
    performanceMonitor.trackError(error);

    try {
      const recoveredUrl = await errorRecoveryManager.recover(error, context);
      fallbackManager.recordSuccess();
      return {
        success: true,
        url: recoveredUrl,
        recovered: true
      };
    } catch (recoveryError) {
      fallbackManager.recordFailure();
      return {
        success: false,
        error: recoveryError.message,
        fallbackExhausted: true
      };
    }
  },

  // Get available quality options
  getQualityOptions: (contentType = 'movie') => {
    const baseQualities = [QualityOptions.Q1080P, QualityOptions.Q720P, QualityOptions.Q480P];

    if (contentType === 'movie') {
      baseQualities.unshift(QualityOptions.Q4K);
    }

    return baseQualities;
  },

  // Test stream availability with enhanced error handling (November 2025)
  testStream: async (url, timeout = 8000) => {
    const startTime = Date.now();

    // Create a timeout promise with AbortController for better control
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), timeout);

    try {
      // Try HEAD request first (more efficient)
      const response = await fetch(url, {
        method: 'HEAD',
        signal: controller.signal,
        headers: {
          'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36'
        }
      });

      clearTimeout(timeoutId);
      const loadTime = Date.now() - startTime;
      performanceMonitor.trackStreamLoadTime(Date.now());

      // Check if response is successful and content exists
      if (response.ok && response.status < 400) {
        return {
          available: true,
          loadTime,
          url,
          responseTime: loadTime,
          method: 'HEAD',
          status: response.status
        };
      } else {
        return {
          available: false,
          loadTime,
          error: `HTTP ${response.status}: ${response.statusText}`,
          errorType: response.status === 404 ? StreamErrorTypes.CONTENT_NOT_FOUND : StreamErrorTypes.PLAYER_ERROR,
          url,
          responseTime: loadTime,
          method: 'HEAD',
          status: response.status,
          retryable: response.status !== 404
        };
      }
    } catch (headError) {
      try {
        // If HEAD fails, try GET request to actually check content
        const getResponse = await fetch(url, {
          method: 'GET',
          signal: controller.signal,
          headers: {
            'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36'
          }
        });

        clearTimeout(timeoutId);
        const loadTime = Date.now() - startTime;
        performanceMonitor.trackStreamLoadTime(Date.now());

        // Check if response is successful
        if (getResponse.ok && getResponse.status < 400) {
          // Try to read response text to verify it's not an error page
          const responseText = await getResponse.text();
          const contentLength = responseText.length;

          // Basic checks for video availability
          const hasVideoContent = contentLength > 1000; // Minimum content length
          const isErrorPage = responseText.includes('not found') ||
                             responseText.includes('video not available') ||
                             responseText.includes('error') ||
                             responseText.includes('404') ||
                             responseText.includes('Video not found');

          if (hasVideoContent && !isErrorPage) {
            return {
              available: true,
              loadTime,
              url,
              responseTime: loadTime,
              method: 'GET',
              status: getResponse.status,
              contentLength
            };
          } else {
            return {
              available: false,
              loadTime,
              error: isErrorPage ? 'Video not found on server' : 'Insufficient content or error page detected',
              errorType: StreamErrorTypes.CONTENT_NOT_FOUND,
              url,
              responseTime: loadTime,
              method: 'GET',
              status: getResponse.status,
              contentLength,
              retryable: false
            };
          }
        } else {
          return {
            available: false,
            loadTime,
            error: `HTTP ${getResponse.status}: ${getResponse.statusText}`,
            errorType: getResponse.status === 404 ? StreamErrorTypes.CONTENT_NOT_FOUND : StreamErrorTypes.PLAYER_ERROR,
            url,
            responseTime: loadTime,
            method: 'GET',
            status: getResponse.status,
            retryable: getResponse.status !== 404
          };
        }
      } catch (getError) {
        clearTimeout(timeoutId);
        const loadTime = Date.now() - startTime;
        performanceMonitor.trackStreamLoadTime(Date.now());

        // Enhanced error classification based on November 2025 standards
        let errorType = StreamErrorTypes.NETWORK_ERROR;
        let errorMessage = getError.message;

        if (getError.name === 'AbortError' || getError.message.includes('timeout') || getError.message.includes('aborted')) {
          errorType = StreamErrorTypes.TIMEOUT_ERROR;
          errorMessage = 'Request timeout - server took too long to respond';
        } else if (getError.message.includes('404') || getError.message.includes('Not Found')) {
          errorType = StreamErrorTypes.CONTENT_NOT_FOUND;
          errorMessage = 'Video not found on this server';
        } else if (getError.message.includes('403') || getError.message.includes('Forbidden')) {
          errorType = StreamErrorTypes.GEO_BLOCKED;
          errorMessage = 'Content blocked in your region';
        } else if (getError.message.includes('NetworkError') || getError.message.includes('Failed to fetch')) {
          errorType = StreamErrorTypes.NETWORK_ERROR;
          errorMessage = 'Network connection failed';
        }

        return {
          available: false,
          loadTime,
          error: errorMessage,
          errorType,
          url,
          responseTime: loadTime,
          retryable: errorType !== StreamErrorTypes.CONTENT_NOT_FOUND
        };
      }
    }
  },

  // Get performance metrics
  getPerformanceReport: () => {
    return performanceMonitor.generateReport();
  },

  // Test multiple sources and return the first working one
  testMultipleSources: async (sources, options = {}) => {
    const { timeout = 8000, maxConcurrent = 3 } = options;
    const workingSources = [];
    const failedSources = [];

    console.log(`üîç [DEBUG] Starting comprehensive source testing`);
    console.log(`üîç [DEBUG] Total sources: ${sources.length}, Timeout: ${timeout}ms, Max concurrent: ${maxConcurrent}`);

    // Test sources in batches to avoid overwhelming servers
    for (let i = 0; i < sources.length; i += maxConcurrent) {
      const batch = sources.slice(i, i + maxConcurrent);
      console.log(`üîç [DEBUG] Testing batch ${Math.floor(i/maxConcurrent) + 1}/${Math.ceil(sources.length/maxConcurrent)}`);

      const batchPromises = batch.map(async (source) => {
        const startTime = Date.now();
        try {
          console.log(`üîç [DEBUG] Testing: ${source.name || source.id} (${source.provider})`);
          const result = await vidsrcApi.testStream(source.url, timeout);
          const testTime = Date.now() - startTime;

          console.log(`üîç [DEBUG] Result: ${result.available ? '‚úÖ' : '‚ùå'} ${source.name || source.id} (${testTime}ms)`);

          return { source, result };
        } catch (error) {
          const testTime = Date.now() - startTime;
          console.log(`üîç [DEBUG] Error: ‚ùå ${source.name || source.id} - ${error.message} (${testTime}ms)`);
          return {
            source,
            result: {
              available: false,
              error: error.message,
              errorType: 'TEST_ERROR'
            }
          };
        }
      });

      const batchResults = await Promise.all(batchPromises);

      batchResults.forEach(({ source, result }) => {
        if (result.available) {
          workingSources.push({ ...source, testResult: result });
          console.log(`‚úÖ [DEBUG] Working source found: ${source.name || source.id}`);
        } else {
          failedSources.push({ ...source, testResult: result });
          console.log(`‚ùå [DEBUG] Failed source: ${source.name || source.id} - ${result.error || 'Unknown error'}`);
        }
      });
    }

    const successRate = sources.length > 0 ? (workingSources.length / sources.length) * 100 : 0;
    console.log(`üìä [DEBUG] Testing complete: ${workingSources.length}/${sources.length} working (${successRate.toFixed(1)}%)`);

    return {
      working: workingSources,
      failed: failedSources,
      total: sources.length,
      successRate
    };
  },

  // Enhanced method to find the first working source quickly
  findFirstWorkingSource: async (sources, options = {}) => {
    const { timeout = 5000, maxConcurrent = 5 } = options;

    console.log(`üîç [DEBUG] Starting source testing for ${sources.length} sources`);
    console.log(`üîç [DEBUG] Timeout: ${timeout}ms, Max concurrent: ${maxConcurrent}`);

    // Test sources in parallel batches
    for (let i = 0; i < sources.length; i += maxConcurrent) {
      const batch = sources.slice(i, i + maxConcurrent);
      console.log(`üîç [DEBUG] Testing batch ${Math.floor(i/maxConcurrent) + 1}: ${batch.map(s => s.name || s.id).join(', ')}`);

      const batchPromises = batch.map(async (source) => {
        const startTime = Date.now();
        try {
          console.log(`üîç [DEBUG] Testing source: ${source.name || source.id} - ${source.url}`);
          const result = await vidsrcApi.testStream(source.url, timeout);

          const testTime = Date.now() - startTime;
          console.log(`üîç [DEBUG] Source ${source.name || source.id}: ${result.available ? '‚úÖ AVAILABLE' : '‚ùå FAILED'} (${testTime}ms)`);

          if (!result.available) {
            console.log(`üîç [DEBUG] Source ${source.name || source.id} failed: ${result.error || 'Unknown error'}`);
          }

          if (result.available) {
            return { source, result, success: true };
          }
          return { source, result, success: false };
        } catch (error) {
          const testTime = Date.now() - startTime;
          console.log(`üîç [DEBUG] Source ${source.name || source.id} error: ${error.message} (${testTime}ms)`);
          return { source, result: { available: false, error: error.message }, success: false };
        }
      });

      const batchResults = await Promise.all(batchPromises);

      // Return the first working source found
      const workingSource = batchResults.find(result => result.success);
      if (workingSource) {
        console.log(`üéâ [DEBUG] Found working source: ${workingSource.source.name || workingSource.source.id}`);
        console.log(`üéâ [DEBUG] URL: ${workingSource.source.url}`);
        console.log(`üéâ [DEBUG] Provider: ${workingSource.source.provider}`);
        return workingSource;
      }
    }

    console.log(`‚ùå [DEBUG] No working sources found after testing all ${sources.length} sources`);
    return null; // No working sources found
  },

  // Get best available source with automatic testing
  getBestAvailableSource: async (tmdbId, type, season = null, episode = null, options = {}) => {
    const { testSources = true, quality = 'auto' } = options;

    // Get all available sources
    const sources = type === 'movie'
      ? vidsrcApi.getMovieSources(tmdbId, { includeAlternatives: true, quality })
      : vidsrcApi.getTVSources(tmdbId, season, episode, { includeAlternatives: true, quality });

    if (!testSources) {
