  // Manual source verification as backup when automatic testing fails
  verifySourceManually: async (url, timeout = 10000) => {
    console.log(`üîç [Manual] Starting manual verification for: ${url}`);

    return new Promise((resolve) => {
      const iframe = document.createElement('iframe');
      iframe.src = url;
      iframe.style.display = 'none';
      iframe.sandbox = 'allow-same-origin allow-scripts allow-presentation';

      const startTime = Date.now();
      let hasLoaded = false;
      let hasError = false;

      const cleanup = () => {
        if (iframe.parentNode) {
          iframe.parentNode.removeChild(iframe);
        }
        clearTimeout(timeoutId);
      };

      const timeoutId = setTimeout(() => {
        if (!hasLoaded && !hasError) {
          console.log(`‚è±Ô∏è [Manual] Manual verification timeout for: ${url}`);
          cleanup();
          resolve({
            available: false,
            error: 'Manual verification timeout',
            errorType: 'TIMEOUT_ERROR',
            method: 'manual-iframe',
            loadTime: Date.now() - startTime
          });
        }
      }, timeout);

      iframe.onload = () => {
        hasLoaded = true;
        const loadTime = Date.now() - startTime;
        console.log(`‚úÖ [Manual] Manual verification success for: ${url} (${loadTime}ms)`);

        // Additional check: try to access iframe content
        try {
          const iframeDoc = iframe.contentDocument || iframe.contentWindow.document;
          const hasVideoElement = iframeDoc.querySelector('video') !== null;
          const hasPlayerContainer = iframeDoc.querySelector('.player, #player, [class*="player"]') !== null;
          const isErrorPage = iframeDoc.body && iframeDoc.body.textContent.includes('not found');

          if (isErrorPage) {
            cleanup();
            resolve({
              available: false,
              error: 'Video not found (detected error page)',
              errorType: 'CONTENT_NOT_FOUND',
              method: 'manual-iframe',
              loadTime,
              hasVideoElement,
              hasPlayerContainer
            });
          } else if (hasVideoElement || hasPlayerContainer) {
            cleanup();
            resolve({
              available: true,
              loadTime,
              method: 'manual-iframe',
              hasVideoElement,
              hasPlayerContainer
            });
          } else {
            // Give it a bit more time to load video content
            setTimeout(() => {
              try {
                const finalDoc = iframe.contentDocument || iframe.contentWindow.document;
                const finalVideoElement = finalDoc.querySelector('video') !== null;
                const finalPlayerContainer = finalDoc.querySelector('.player, #player, [class*="player"]') !== null;

                cleanup();
                resolve({
                  available: finalVideoElement || finalPlayerContainer,
                  loadTime: Date.now() - startTime,
                  method: 'manual-iframe',
                  hasVideoElement: finalVideoElement,
                  hasPlayerContainer: finalPlayerContainer
                });
              } catch (e) {
                cleanup();
                resolve({
                  available: true, // Assume it's working if we can't check content
                  loadTime: Date.now() - startTime,
                  method: 'manual-iframe',
                  note: 'Could not verify content due to CORS, assuming available'
                });
              }
            }, 2000);
          }
        } catch (e) {
          // CORS restriction - assume it's working if it loaded
          cleanup();
          resolve({
            available: true,
            loadTime,
            method: 'manual-iframe',
            note: 'Could not verify content due to CORS, assuming available'
          });
        }
      };

      iframe.onerror = () => {
        hasError = true;
        const loadTime = Date.now() - startTime;
        console.log(`‚ùå [Manual] Manual verification failed for: ${url} (${loadTime}ms)`);
        cleanup();
        resolve({
          available: false,
          error: 'Failed to load iframe',
          errorType: 'NETWORK_ERROR',
          method: 'manual-iframe',
          loadTime
        });
      };

      document.body.appendChild(iframe);
    });
  },

  // Create secure iframe embed
  createSecureEmbed: (streamData, options = {}) => {
    const {
      width = '100%',
      height = '500',
      autoplay = false,
      muted = false,
      controls = true,
      sandbox = 'allow-same-origin allow-scripts allow-presentation'
    } = options;

    const embedUrl = streamData.url;

    return {
      iframe: `<iframe
        src="${embedUrl}"
        width="${width}"
        height="${height}"
        frameborder="0"
        allowfullscreen
        ${autoplay ? 'autoplay' : ''}
        ${muted ? 'muted' : ''}
        ${controls ? '' : 'controls="false"'}
        sandbox="${sandbox}"
        allow="autoplay; encrypted-media; fullscreen; picture-in-picture"
        referrerpolicy="strict-origin-when-cross-origin"
        loading="lazy"
      ></iframe>`,
      url: embedUrl,
      type: streamData.type,
      provider: streamData.provider,
      security: {
        sandbox,
        referrerPolicy: 'strict-origin-when-cross-origin',
        cspCompliant: true
      }
    };
  }
};

