// VidSrc API service for streaming integration
// No API key required - uses TMDB IDs directly

// Enhanced domain configuration with multiple fallback levels (November 2025)
const VIDSRC_DOMAINS = {
  primary: 'https://vidsrc.to/embed',
  fallbacks: [
    'https://vidsrc.cc/embed',
    'https://vidsrc.org/embed',
    'https://vidsrc.me/embed',
    'https://vidsrc.net/embed',
    'https://vidsrc.xyz/embed',
    'https://vidsrc.io/embed',
    'https://vidsrc.vc/embed',
    'https://vidsrc.bz/embed',
    'https://vidsrc.gd/embed'
  ],
  alternative: [
    'https://embedsu.com/embed',
    'https://2embed.cc/embed',
    'https://autoembed.cc/embed',
    'https://vidsrcsu.com/embed',
    'https://vidsrc.pk/embed',
    'https://www.2embed.cc/embed',
    'https://embed.su/embed',
    'https://autoembed.cc/embed'
  ]
};

// Quality options for streaming
const QualityOptions = {
  AUTO: 'auto',
  Q4K: '4k',
  Q1080P: '1080p',
  Q720P: '720p',
  Q480P: '480p',
  Q360P: '360p'
};

// Error types for better error handling
const StreamErrorTypes = {
  NETWORK_ERROR: 'NETWORK_ERROR',
  TIMEOUT_ERROR: 'TIMEOUT_ERROR',
  PLAYER_ERROR: 'PLAYER_ERROR',
  CONTENT_NOT_FOUND: 'CONTENT_NOT_FOUND',
  GEO_BLOCKED: 'GEO_BLOCKED',
  QUALITY_UNAVAILABLE: 'QUALITY_UNAVAILABLE'
};

// Alternative streaming sources for fallback (November 2025)
const EMBED_SOURCES = {
  vidsrc: {
    name: 'VidSrc',
    baseUrl: 'https://vidsrc.me/embed',
    supports: ['movie', 'tv']
  },
  vidsrcdomains: {
    name: 'VidSrc Domains',
    baseUrl: 'https://vidsrc.domains/embed/',
    supports: ['movie', 'tv']
  },
  embedsu: {
    name: 'EmbedSu',
    baseUrl: 'https://embedsu.com/embed/',
    supports: ['movie', 'tv']
  },
  embed2: {
    name: '2Embed',
    baseUrl: 'https://2embed.cc/embed/',
    supports: ['movie', 'tv']
  },
  autoembed: {
    name: 'AutoEmbed',
    baseUrl: 'https://autoembed.cc/embed/',
    supports: ['movie', 'tv']
  },
  vidsrcsu: {
    name: 'VidSrcSu',
    baseUrl: 'https://vidsrcsu.com/embed/',
    supports: ['movie', 'tv']
  },
  vidsrcpk: {
    name: 'VidSrcPK',
    baseUrl: 'https://vidsrc.pk/embed/',
    supports: ['movie', 'tv']
  },
  embedsoap: {
    name: 'EmbedSoap',
    baseUrl: 'https://www.embedsoap.com/embed/movie/',
    supports: ['movie']
  },
  smashystream: {
    name: 'SmashyStream',
    baseUrl: 'https://player.smashystream.com/movie/',
    supports: ['movie']
  },
  multiembed: {
    name: 'MultiEmbed',
    baseUrl: 'https://multiembed.mov/directstream.php?video_id=',
    supports: ['movie', 'tv']
  },
  vidplay: {
    name: 'VidPlay',
    baseUrl: 'https://vidplay.online/embed/',
    supports: ['movie', 'tv']
  },
  solarmovie: {
    name: 'SolarMovie',
    baseUrl: 'https://www.solarmovie.pe/embed/',
    supports: ['movie', 'tv']
  },
  flixhq: {
    name: 'FlixHQ',
    baseUrl: 'https://flixhq.to/embed/',
    supports: ['movie', 'tv']
  },
  solarmovie: {
    name: 'SolarMovie',
    baseUrl: 'https://www.solarmovie.pe/embed/',
    supports: ['movie', 'tv']
  },
  fmovies: {
    name: 'FMovies',
    baseUrl: 'https://fmovies.to/embed/',
    supports: ['movie', 'tv']
  },
  lookmovie: {
    name: 'LookMovie',
    baseUrl: 'https://lookmovie.io/embed/',
    supports: ['movie', 'tv']
  },
  sflix: {
    name: 'SFlix',
    baseUrl: 'https://sflix.to/embed/',
    supports: ['movie', 'tv']
  }
};

// Fallback Manager Class
class FallbackManager {
  constructor() {
    this.currentIndex = 0;
    this.retryCount = 0;
    this.maxRetries = 3;
    this.domainLevel = 0; // 0 = primary, 1 = fallbacks, 2 = alternative
  }

  getDomainsForCurrentLevel() {
    switch (this.domainLevel) {
      case 0:
        return [VIDSRC_DOMAINS.primary];
      case 1:
        return VIDSRC_DOMAINS.fallbacks;
      case 2:
        return VIDSRC_DOMAINS.alternative;
      default:
        return [VIDSRC_DOMAINS.primary];
    }
  }

  getNextUrl(type, id, season = null, episode = null, quality = 'auto') {
    const domains = this.getDomainsForCurrentLevel();
    const domain = domains[this.currentIndex % domains.length];

    this.currentIndex++;

    // Move to next domain level if we've tried all domains in current level
    if (this.currentIndex >= domains.length) {
      this.currentIndex = 0;
      this.domainLevel = Math.min(this.domainLevel + 1, 2);
    }

    // Use correct URL patterns based on research (November 2025)
    let baseUrl;

    // Different URL patterns for different domains
    if (domain.includes('vidsrc.to') || domain.includes('vidsrc.cc') || domain.includes('vidsrc.org')) {
      // These domains use the /embed/movie/ID pattern
      baseUrl = type === 'movie'
        ? `${domain}/movie/${id}`
        : `${domain}/tv/${id}/${season}/${episode}`;
    } else if (domain.includes('vidsrc.me')) {
      // vidsrc.me uses direct embed pattern
      baseUrl = type === 'movie'
        ? `${domain}/movie/${id}`
        : `${domain}/tv/${id}/${season}/${episode}`;
    } else {
      // Default pattern for other domains
      baseUrl = type === 'movie'
        ? `${domain}/movie/${id}`
        : `${domain}/tv/${id}/${season}/${episode}`;
    }

    return quality !== 'auto' ? `${baseUrl}?q=${quality}` : baseUrl;
  }

  recordFailure() {
    this.retryCount++;
    if (this.retryCount >= this.maxRetries) {
      this.reset();
      throw new Error('All streaming sources failed');
    }
  }

  recordSuccess() {
    this.retryCount = 0;
    this.currentIndex = 0;
    this.domainLevel = 0;
  }

  reset() {
    this.currentIndex = 0;
    this.retryCount = 0;
    this.domainLevel = 0;
  }

  getCurrentDomain() {
    const domains = this.getDomainsForCurrentLevel();
    return domains[this.currentIndex % domains.length];
  }
}

// Error Recovery Manager Class
class ErrorRecoveryManager {
  constructor(fallbackManager) {
    this.fallbackManager = fallbackManager;
    this.recoveryStrategies = new Map();
    this.initializeStrategies();
  }

  initializeStrategies() {
    this.recoveryStrategies.set(StreamErrorTypes.NETWORK_ERROR, this.handleNetworkError.bind(this));
    this.recoveryStrategies.set(StreamErrorTypes.TIMEOUT_ERROR, this.handleTimeoutError.bind(this));
    this.recoveryStrategies.set(StreamErrorTypes.PLAYER_ERROR, this.handlePlayerError.bind(this));
    this.recoveryStrategies.set(StreamErrorTypes.CONTENT_NOT_FOUND, this.handleContentNotFound.bind(this));
    this.recoveryStrategies.set(StreamErrorTypes.QUALITY_UNAVAILABLE, this.handleQualityError.bind(this));
  }

  async recover(error, context) {
    const strategy = this.recoveryStrategies.get(error.type);
    if (strategy) {
      return await strategy(error, context);
    }
    throw error;
  }

  async handleNetworkError(error, context) {
    // Wait with exponential backoff
    const delay = this.calculateBackoffDelay(context.retryCount);
    await this.delay(delay);

    return this.fallbackManager.getNextUrl(
      context.type,
      context.id,
      context.season,
      context.episode,
      context.quality
    );
  }

  async handleTimeoutError(error, context) {
    // Reduce quality and retry
    const newQuality = this.reduceQuality(context.quality);
    return this.fallbackManager.getNextUrl(
      context.type,
      context.id,
      context.season,
      context.episode,
      newQuality
    );
  }

  async handlePlayerError(error, context) {
    // Try different domain
    return this.fallbackManager.getNextUrl(
      context.type,
      context.id,
      context.season,
      context.episode,
      context.quality
    );
  }

  async handleContentNotFound(error, context) {
    // Try alternative sources
    this.fallbackManager.domainLevel = 2; // Use alternative domains
    return this.fallbackManager.getNextUrl(
      context.type,
      context.id,
      context.season,
      context.episode,
      context.quality
    );
  }

  async handleQualityError(error, context) {
    // Reduce quality and retry
    const newQuality = this.reduceQuality(context.quality);
    return this.fallbackManager.getNextUrl(
      context.type,
      context.id,
      context.season,
      context.episode,
      newQuality
    );
  }

  calculateBackoffDelay(retryCount) {
    // Enhanced exponential backoff with jitter (November 2025)
    const baseDelay = 1000 * Math.pow(2, retryCount);
    const jitter = Math.random() * 1000; // Add random jitter up to 1 second
    return Math.min(baseDelay + jitter, 15000); // Max 15 seconds
  }

  delay(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  // Enhanced quality reduction strategy
  reduceQuality(currentQuality) {
    const qualityHierarchy = [QualityOptions.Q4K, QualityOptions.Q1080P, QualityOptions.Q720P, QualityOptions.Q480P, QualityOptions.Q360P];
    const currentIndex = qualityHierarchy.indexOf(currentQuality);

    if (currentIndex < qualityHierarchy.length - 1) {
      return qualityHierarchy[currentIndex + 1];
    }

    return QualityOptions.Q360P;
  }

  reduceQuality(currentQuality) {
    const qualityHierarchy = [QualityOptions.Q4K, QualityOptions.Q1080P, QualityOptions.Q720P, QualityOptions.Q480P, QualityOptions.Q360P];
    const currentIndex = qualityHierarchy.indexOf(currentQuality);

    if (currentIndex < qualityHierarchy.length - 1) {
      return qualityHierarchy[currentIndex + 1];
    }

    return QualityOptions.Q360P;
  }
}

// Performance Monitor Class
class PerformanceMonitor {
  constructor() {
    this.metrics = {
      streamLoadTime: [],
      bufferingEvents: [],
      qualitySwitches: [],
      errorRates: [],
      userEngagement: []
    };
    this.startTime = null;
  }

  startTracking() {
    this.startTime = Date.now();
  }

  trackStreamLoadTime(endTime) {
    if (!this.startTime) return;

    const loadTime = endTime - this.startTime;
    this.metrics.streamLoadTime.push(loadTime);

    if (loadTime > 10000) { // 10 seconds
      console.warn('Slow stream loading detected:', loadTime);
    }
  }

  trackBufferingEvent(duration) {
    this.metrics.bufferingEvents.push({
      timestamp: Date.now(),
      duration
    });
  }

  trackQualitySwitch(from, to) {
    this.metrics.qualitySwitches.push({
      timestamp: Date.now(),
      from,
      to
    });
  }

  trackError(error) {
    this.metrics.errorRates.push({
      timestamp: Date.now(),
      error: error.message
    });
  }

  generateReport() {
    const loadTimes = this.metrics.streamLoadTime;
    return {
      averageLoadTime: loadTimes.length > 0 ? loadTimes.reduce((a, b) => a + b, 0) / loadTimes.length : 0,
      bufferingFrequency: this.metrics.bufferingEvents.length,
      qualitySwitchCount: this.metrics.qualitySwitches.length,
      errorCount: this.metrics.errorRates.length,
      successRate: this.calculateSuccessRate()
    };
  }

  calculateSuccessRate() {
    const totalAttempts = this.metrics.streamLoadTime.length + this.metrics.errorRates.length;
    return totalAttempts > 0 ? (this.metrics.streamLoadTime.length / totalAttempts) * 100 : 0;
  }
}

// Global instances
const fallbackManager = new FallbackManager();
const errorRecoveryManager = new ErrorRecoveryManager(fallbackManager);
const performanceMonitor = new PerformanceMonitor();

export const vidsrcApi = {
  // Enhanced streaming URL generation with fallback support
  getMovieStream: (tmdbId, options = {}) => {
    const { quality = 'auto', useFallback = true } = options;

    try {
      const url = useFallback
        ? fallbackManager.getNextUrl('movie', tmdbId, null, null, quality)
        : `${VIDSRC_DOMAINS.primary}/movie/${tmdbId}${quality !== 'auto' ? `?q=${quality}` : ''}`;

      return {
        url,
        type: 'movie',
        provider: 'vidsrc',
        embeddable: true,
        quality,
        fallbackSupported: useFallback
      };
    } catch (error) {
      return this.getErrorStream('movie', tmdbId, error);
    }
  },

  // Enhanced TV streaming with fallback support
  getTVStream: (tmdbId, season = 1, episode = 1, options = {}) => {
    const { quality = 'auto', useFallback = true } = options;

    try {
      const url = useFallback
        ? fallbackManager.getNextUrl('tv', tmdbId, season, episode, quality)
        : `${VIDSRC_DOMAINS.primary}/tv/${tmdbId}/${season}/${episode}${quality !== 'auto' ? `?q=${quality}` : ''}`;

      return {
        url,
        type: 'tv',
        provider: 'vidsrc',
        embeddable: true,
        season,
        episode,
        quality,
        fallbackSupported: useFallback
      };
    } catch (error) {
      return this.getErrorStream('tv', tmdbId, error, season, episode);
    }
  },

  // Get error stream for failed requests
  getErrorStream: (type, id, error, season = null, episode = null) => {
    return {
      url: '',
      type,
      provider: 'error',
      embeddable: false,
      error: error.message,
      season,
      episode
    };
  },

  // Try alternative URL patterns for better compatibility (November 2025)
  tryAlternativeUrls: (tmdbId, type, season = null, episode = null) => {
    const alternatives = [];

    // Try current working VidSrc domains from research
    const domains = [
      'https://vidsrc.me/embed',
      'https://vidsrc.in/embed',
      'https://vidsrc.net/embed',
      'https://vidsrc.xyz/embed',
      'https://vidsrc.io/embed',
      'https://vidsrc.vc/embed',
      'https://vidsrc.bz/embed',
      'https://vidsrc.gd/embed',
      'https://vidsrc.do/embed',
      'https://vidsrc.mn/embed',
      'https://vidsrc.tw/embed'
    ];

    domains.forEach(domain => {
      if (type === 'movie') {
        alternatives.push(`${domain}/movie/${tmdbId}`);
      } else {
        alternatives.push(`${domain}/tv/${tmdbId}/${season || 1}/${episode || 1}`);
      }
    });

    // Add alternative streaming sources
    const altSources = [
      `https://embedsu.com/embed/${tmdbId}`,
      `https://2embed.cc/embed/${tmdbId}`,
      `https://autoembed.cc/embed/${tmdbId}`,
      `https://vidsrcsu.com/embed/${tmdbId}`
    ];

    alternatives.push(...altSources);

    return alternatives;
  },

  // Enhanced multiple streaming sources with quality options
  getMovieSources: (tmdbId, options = {}) => {
    const { includeAlternatives = true, quality = 'auto' } = options;
    const sources = [];

    // Primary VidSrc sources with different qualities
    const qualities = [QualityOptions.Q4K, QualityOptions.Q1080P, QualityOptions.Q720P, QualityOptions.Q480P];

    qualities.forEach((q, index) => {
