  // Enhanced TV sources with quality options
  getTVSources: (tmdbId, season = 1, episode = 1, options = {}) => {
    const { includeAlternatives = true, quality = 'auto' } = options;
    const sources = [];

    // Primary VidSrc sources with different qualities
    const qualities = [QualityOptions.Q1080P, QualityOptions.Q720P, QualityOptions.Q480P];

    qualities.forEach((q, index) => {
      sources.push({
        id: `vidsrc-${q}`,
        name: `VidSrc ${q.toUpperCase()}`,
        url: fallbackManager.getNextUrl('tv', tmdbId, season, episode, q),
        embeddable: true,
        quality: q,
        provider: 'vidsrc',
        season,
        episode,
        primary: index === 0
      });
    });

    // Backup domains for primary quality
    if (includeAlternatives) {
      VIDSRC_DOMAINS.fallbacks.forEach((domain, index) => {
        sources.push({
          id: `vidsrc-backup-${index}`,
          name: `VidSrc Backup ${index + 1}`,
          url: `${domain}/tv/${tmdbId}/${season}/${episode}${quality !== 'auto' ? `?q=${quality}` : ''}`,
          embeddable: true,
          quality: quality,
          provider: 'vidsrc',
          season,
          episode,
          backup: true
        });
      });
    }

    return sources;
  },

