  // Enhanced error handling with recovery
  handleStreamError: async (error, context) => {
    performanceMonitor.trackError(error);

    try {
      const recoveredUrl = await errorRecoveryManager.recover(error, context);
      fallbackManager.recordSuccess();
      return {
