import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { FiArrowLeft, FiPlay, FiPause, FiVolume2, FiVolumeX, FiMaximize, FiSettings, FiRotateCcw, FiAlertTriangle, FiCheckCircle, FiLoader } from 'react-icons/fi';
import { moviesApi, tvApi } from '../../services/tmdbApi';
import { vidsrcApi, createEmbedCode } from '../../services/vidsrcApi';
import { useSettings } from '../../context/SettingsContext';
import { getImageUrl } from '../../services/tmdbApi';
import './Player.css';

const Player = () => {
  const { type, id } = useParams();
  const navigate = useNavigate();
  const { settings } = useSettings();
  const iframeRef = useRef(null);
  const controlsTimeoutRef = useRef(null);

  // Enhanced state management
  const [content, setContent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [loadingPhase, setLoadingPhase] = useState('initializing');
  const [error, setError] = useState(null);
  const [streamData, setStreamData] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [isMuted, setIsMuted] = useState(false);
  const [showControls, setShowControls] = useState(true);
  const [quality, setQuality] = useState('auto');
  const [availableQualities, setAvailableQualities] = useState([]);
  const [showQualitySelector, setShowQualitySelector] = useState(false);
  const [retryCount, setRetryCount] = useState(0);
  const [isMobile, setIsMobile] = useState(false);
  const [touchControls, setTouchControls] = useState({
    brightness: 50,
    volume: 50,
    seeking: false
  });
  const [iframeLoaded, setIframeLoaded] = useState(false);
  const [iframeError, setIframeError] = useState(false);

  useEffect(() => {
    loadContent();
    detectMobileDevice();

    return () => {
      if (controlsTimeoutRef.current) {
        clearTimeout(controlsTimeoutRef.current);
      }
    };
  }, [type, id]);

  useEffect(() => {
    if (content) {
      loadStream();
    }
  }, [content, quality]);

  // Detect mobile device
  const detectMobileDevice = () => {
    const mobile = /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent) ||
                   window.innerWidth <= 768;
    setIsMobile(mobile);
  };

  const loadContent = async () => {
    try {
      setLoading(true);
      setLoadingPhase('initializing');
      setError(null);

      let contentData;
      if (type === 'movie') {
        setLoadingPhase('fetching_metadata');
        contentData = await moviesApi.getDetails(id);
      } else {
        setLoadingPhase('fetching_metadata');
        contentData = await tvApi.getDetails(id);
      }

      setContent(contentData);
      setAvailableQualities(vidsrcApi.getQualityOptions(type));
    } catch (err) {
      console.error('Error loading content:', err);
      setError('Failed to load content details');
    } finally {
      setLoading(false);
    }
  };

  const loadStream = async () => {
    try {
      setLoadingPhase('loading_stream');
      setError(null);

      let stream;
      if (type === 'movie') {
        console.log(`ðŸŽ¬ [Player] Loading movie stream for ${id}`);
        stream = vidsrcApi.getMovieStream(id, { quality });
      } else {
        console.log(`ðŸ“º [Player] Loading TV stream for ${id}`);
        // Default to season 1 episode 1 for TV shows
        stream = vidsrcApi.getTVStream(id, 1, 1, { quality });
      }

      setStreamData(stream);
      setLoadingPhase('preparing_player');
      setLoadingPhase('ready');
      console.log(`âœ… [Player] Stream loaded successfully: ${stream.url}`);
    } catch (err) {
      console.error('Error loading stream:', err);
      await handleStreamError(err);
    }
  };

  const handleStreamError = async (error) => {
    console.error('Stream loading error:', error);

    // For single domain setup, just show a simple error message
    setError(`Unable to load stream: ${error.message || 'Unknown error occurred'}. Please try again later.`);
  };

  const handlePlayPause = () => {
    setIsPlaying(!isPlaying);
  };

  const handleMuteToggle = () => {
    setIsMuted(!isMuted);
  };

  const handleMouseMove = () => {
    setShowControls(true);

    if (controlsTimeoutRef.current) {
      clearTimeout(controlsTimeoutRef.current);
    }

    const timeout = setTimeout(() => {
      setShowControls(false);
    }, 3000);

    controlsTimeoutRef.current = timeout;
  };

  const handleFullscreen = () => {
    if (document.fullscreenElement) {
      document.exitFullscreen();
    } else {
      document.documentElement.requestFullscreen();
    }
  };

  // Enhanced quality selection
  const handleQualityChange = async (newQuality) => {
    setQuality(newQuality);
    setShowQualitySelector(false);

    // Track quality switch
    if (streamData) {
      // This would be implemented in the vidsrcApi service
      console.log(`Quality changed from ${quality} to ${newQuality}`);
    }
  };

  // Retry stream loading
  const handleRetry = () => {
    setError(null);
    setRetryCount(0);
    loadStream();
  };

  // Iframe load handler
  const handleIframeLoad = () => {
    setIframeLoaded(true);
    setIframeError(false);
    console.log('Video iframe loaded successfully');
  };

  // Iframe error handler
  const handleIframeError = () => {
    setIframeLoaded(false);
    setIframeError(true);
    console.error('Video iframe failed to load');
  };

  // Mobile touch controls
  const handleTouchStart = (e) => {
    if (!isMobile) return;

    const touch = e.touches[0];
    const rect = e.currentTarget.getBoundingClientRect();
    const x = touch.clientX - rect.left;
    const y = touch.clientY - rect.top;

    // Detect touch zones
    if (x < rect.width * 0.3) {
      // Left side - brightness/volume control
      setTouchControls(prev => ({ ...prev, seeking: false }));
    } else if (x > rect.width * 0.7) {
      // Right side - seeking
      setTouchControls(prev => ({ ...prev, seeking: true }));
    }
  };

  const handleTouchMove = (e) => {
    if (!isMobile || !touchControls.seeking) return;

    e.preventDefault();
    // Handle seeking logic would go here
  };

  useEffect(() => {
    const interval = setInterval(updatePerformanceMetrics, 5000);
    return () => clearInterval(interval);
  }, [updatePerformanceMetrics]);

  if (loading) {
    return (
      <div className="player-loading">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <div className="loading-progress">
            <div className="loading-text">
              {loadingPhase === 'initializing' && 'Initializing player...'}
              {loadingPhase === 'fetching_metadata' && 'Loading content details...'}
              {loadingPhase === 'loading_stream' && 'Finding best streaming source...'}
              {loadingPhase === 'finding_best_source' && 'Finding best streaming source...'}
              {loadingPhase.startsWith('testing_') && `Testing ${loadingPhase.replace('testing_', '').toUpperCase()}...`}
              {loadingPhase === 'testing_alternative' && 'Trying alternative sources...'}
              {loadingPhase === 'preparing_player' && 'Setting up player...'}
              {loadingPhase === 'ready' && 'Ready to play!'}
            </div>
            <div className="loading-bar">
              <div
                className="loading-fill"
                style={{
                  width: loadingPhase === 'initializing' ? '10%' :
                         loadingPhase === 'fetching_metadata' ? '30%' :
                         loadingPhase === 'loading_stream' ? '50%' :
                         loadingPhase === 'finding_best_source' ? '75%' :
                         loadingPhase === 'preparing_player' ? '90%' : '100%'
                }}
              ></div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="player-error">
        <div className="error-content">
          <div className="error-icon">
            <FiAlertTriangle />
          </div>
          <h2>Unable to Load Stream</h2>
          <p>{error}</p>


          <div className="error-actions">
            <button onClick={handleRetry} className="retry-btn">
              <FiRotateCcw />
              Try Again
            </button>
            <Link to="/" className="back-btn">
              <FiArrowLeft />
              Back to Home
            </Link>
          </div>
        </div>
      </div>
    );
  }

  if (!content || !streamData) {
    return (
      <div className="player-error">
        <div className="error-content">
          <h2>Content Not Found</h2>
          <p>The requested content could not be found.</p>
          <Link to="/" className="back-btn">
            <FiArrowLeft />
            Back to Home
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="player-container" onMouseMove={handleMouseMove}>
      {/* Player Header */}
      <div className={`player-header ${showControls ? 'visible' : 'hidden'}`}>
        <div className="header-left">
          <button
            className="back-btn"
            onClick={() => navigate(-1)}
            aria-label="Go back"
          >
            <FiArrowLeft />
          </button>
          <div className="content-info">
            <h1 className="content-title">{content.title}</h1>
            {content.releaseDate && (
              <span className="content-year">
                {new Date(content.releaseDate).getFullYear()}
              </span>
            )}
          </div>
        </div>

        <div className="header-right">
          <div className="quality-selector">
            <button
              className="quality-btn"
              onClick={() => setShowQualitySelector(!showQualitySelector)}
              aria-label="Quality settings"
            >
              {quality.toUpperCase()}
            </button>
            {showQualitySelector && (
              <div className="quality-dropdown">
                {availableQualities.map((q) => (
                  <button
                    key={q}
                    className={`quality-option ${quality === q ? 'active' : ''}`}
                    onClick={() => handleQualityChange(q)}
                  >
                    {q.toUpperCase()}
                    {quality === q && <FiCheckCircle />}
                  </button>
                ))}
              </div>
            )}
          </div>
          <button className="settings-btn" aria-label="Settings">
            <FiSettings />
          </button>
        </div>
      </div>

      {/* Video Player */}
      <div
        className={`video-wrapper ${isMobile ? 'mobile' : ''}`}
        onTouchStart={handleTouchStart}
        onTouchMove={handleTouchMove}
      >
        <iframe
          ref={iframeRef}
          src={streamData.url}
          title={`${content.title} - Player`}
          frameBorder="0"
          allowFullScreen
          allow="autoplay; encrypted-media; fullscreen; picture-in-picture"
          sandbox="allow-same-origin allow-scripts allow-presentation"
          className="video-player"
          loading="lazy"
          referrerPolicy="strict-origin-when-cross-origin"
          onLoad={handleIframeLoad}
          onError={handleIframeError}
        />

        {/* Mobile touch overlay */}
        {isMobile && (
          <div className="mobile-controls-overlay">
            <div className="touch-zones">
              <div className="touch-zone left"></div>
              <div className="touch-zone center"></div>
              <div className="touch-zone right"></div>
            </div>
          </div>
        )}

        {/* Video Controls Overlay */}
        <div className={`video-controls ${showControls ? 'visible' : 'hidden'}`}>
          <div className="controls-center">
            <button
              className="control-btn play-pause"
              onClick={handlePlayPause}
              aria-label={isPlaying ? 'Pause' : 'Play'}
            >
              {isPlaying ? <FiPause /> : <FiPlay />}
            </button>
          </div>

          <div className="controls-bottom">
            <div className="controls-left">
              <button
                className="control-btn"
                onClick={handleMuteToggle}
                aria-label={isMuted ? 'Unmute' : 'Mute'}
              >
                {isMuted ? <FiVolumeX /> : <FiVolume2 />}
              </button>
            </div>

            <div className="controls-right">
              {performanceMetrics && (
                <div className="performance-indicator">
                  <span className={`status-dot ${performanceMetrics.successRate > 90 ? 'good' : performanceMetrics.successRate > 70 ? 'warning' : 'error'}`}></span>
                  <span className="performance-text">
                    {Math.round(performanceMetrics.averageLoadTime)}ms
                  </span>
                </div>
              )}
              {iframeLoaded && (
                <div className="stream-status">
                  <span className="status-dot good"></span>
                  <span className="status-text">LIVE</span>
                </div>
              )}
              {iframeError && (
                <div className="stream-status error">
                  <span className="status-dot error"></span>
                  <span className="status-text">ERROR</span>
                </div>
              )}
              <button
                className="control-btn fullscreen"
                onClick={handleFullscreen}
                aria-label="Toggle fullscreen"
              >
                <FiMaximize />
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Content Info Panel */}
      <div className="content-panel">
        <div className="panel-poster">
          <img
            src={getImageUrl(content.posterPath)}
            alt={content.title}
            loading="lazy"
          />
        </div>

        <div className="panel-info">
          <h2 className="panel-title">{content.title}</h2>

          <div className="panel-meta">
            {content.voteAverage > 0 && (
              <div className="panel-rating">
                <span className="rating-star">â­</span>
                <span>{content.voteAverage.toFixed(1)}</span>
              </div>
            )}
            {content.releaseDate && (
              <span className="panel-year">
                {new Date(content.releaseDate).getFullYear()}
              </span>
            )}
            {content.runtime && (
              <span className="panel-runtime">
                {content.runtime} min
              </span>
            )}
          </div>

          {content.overview && (
            <p className="panel-description">
              {content.overview}
            </p>
          )}

          <div className="panel-actions">
            <Link
              to={`/${type}/${id}`}
              className="more-info-btn"
            >
              More Information
            </Link>
          </div>

          {/* Stream Information */}
          {streamData && (
            <div className="stream-info">
              <h4>Stream Details</h4>
              <div className="stream-details">
                <span>Quality: {quality.toUpperCase()}</span>
                <span>Provider: {streamData.provider}</span>
              </div>
            </div>
          )}

          {/* Performance Metrics */}
          {performanceMetrics && (
            <div className="performance-info">
              <h4>Performance</h4>
              <div className="performance-details">
                <span>Load Time: {Math.round(performanceMetrics.averageLoadTime)}ms</span>
                <span>Success Rate: {Math.round(performanceMetrics.successRate)}%</span>
                <span>Buffering Events: {performanceMetrics.bufferingFrequency}</span>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Player;
