  // Test stream availability with enhanced error handling (November 2025)
  testStream: async (url, timeout = 8000) => {
    const startTime = Date.now();

    // Create a timeout promise with AbortController for better control
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), timeout);

    try {
      // Try HEAD request first (more efficient)
      const response = await fetch(url, {
        method: 'HEAD',
        signal: controller.signal,
        headers: {
          'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36'
        }
      });

      clearTimeout(timeoutId);
      const loadTime = Date.now() - startTime;
      performanceMonitor.trackStreamLoadTime(Date.now());

      // Check if response is successful and content exists
      if (response.ok && response.status < 400) {
        return {
          available: true,
          loadTime,
          url,
          responseTime: loadTime,
          method: 'HEAD',
          status: response.status
        };
      } else {
        return {
          available: false,
          loadTime,
          error: `HTTP ${response.status}: ${response.statusText}`,
          errorType: response.status === 404 ? StreamErrorTypes.CONTENT_NOT_FOUND : StreamErrorTypes.PLAYER_ERROR,
          url,
          responseTime: loadTime,
          method: 'HEAD',
          status: response.status,
          retryable: response.status !== 404
        };
      }
    } catch (headError) {
      try {
        // If HEAD fails, try GET request to actually check content
        const getResponse = await fetch(url, {
          method: 'GET',
          signal: controller.signal,
          headers: {
            'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36'
          }
        });

        clearTimeout(timeoutId);
        const loadTime = Date.now() - startTime;
        performanceMonitor.trackStreamLoadTime(Date.now());

        // Check if response is successful
        if (getResponse.ok && getResponse.status < 400) {
          // Try to read response text to verify it's not an error page
          const responseText = await getResponse.text();
          const contentLength = responseText.length;

          // Basic checks for video availability
          const hasVideoContent = contentLength > 1000; // Minimum content length
          const isErrorPage = responseText.includes('not found') ||
                             responseText.includes('video not available') ||
                             responseText.includes('error') ||
                             responseText.includes('404') ||
                             responseText.includes('Video not found');

          if (hasVideoContent && !isErrorPage) {
            return {
              available: true,
              loadTime,
              url,
              responseTime: loadTime,
              method: 'GET',
              status: getResponse.status,
              contentLength
            };
          } else {
            return {
              available: false,
              loadTime,
              error: isErrorPage ? 'Video not found on server' : 'Insufficient content or error page detected',
              errorType: StreamErrorTypes.CONTENT_NOT_FOUND,
              url,
              responseTime: loadTime,
              method: 'GET',
              status: getResponse.status,
              contentLength,
              retryable: false
            };
          }
        } else {
          return {
            available: false,
            loadTime,
            error: `HTTP ${getResponse.status}: ${getResponse.statusText}`,
            errorType: getResponse.status === 404 ? StreamErrorTypes.CONTENT_NOT_FOUND : StreamErrorTypes.PLAYER_ERROR,
            url,
            responseTime: loadTime,
            method: 'GET',
            status: getResponse.status,
            retryable: getResponse.status !== 404
          };
        }
      } catch (getError) {
        clearTimeout(timeoutId);
        const loadTime = Date.now() - startTime;
        performanceMonitor.trackStreamLoadTime(Date.now());

        // Enhanced error classification based on November 2025 standards
        let errorType = StreamErrorTypes.NETWORK_ERROR;
        let errorMessage = getError.message;

        if (getError.name === 'AbortError' || getError.message.includes('timeout') || getError.message.includes('aborted')) {
          errorType = StreamErrorTypes.TIMEOUT_ERROR;
          errorMessage = 'Request timeout - server took too long to respond';
        } else if (getError.message.includes('404') || getError.message.includes('Not Found')) {
          errorType = StreamErrorTypes.CONTENT_NOT_FOUND;
          errorMessage = 'Video not found on this server';
        } else if (getError.message.includes('403') || getError.message.includes('Forbidden')) {
          errorType = StreamErrorTypes.GEO_BLOCKED;
          errorMessage = 'Content blocked in your region';
        } else if (getError.message.includes('NetworkError') || getError.message.includes('Failed to fetch')) {
          errorType = StreamErrorTypes.NETWORK_ERROR;
          errorMessage = 'Network connection failed';
        }

        return {
          available: false,
          loadTime,
          error: errorMessage,
          errorType,
          url,
          responseTime: loadTime,
          retryable: errorType !== StreamErrorTypes.CONTENT_NOT_FOUND
        };
      }
    }
  },

  // Get performance metrics
  getPerformanceReport: () => {
    return performanceMonitor.generateReport();
  },

  // Test multiple sources and return the first working one
  testMultipleSources: async (sources, options = {}) => {
    const { timeout = 8000, maxConcurrent = 3 } = options;
    const workingSources = [];
    const failedSources = [];

    console.log(`🔍 [DEBUG] Starting comprehensive source testing`);
    console.log(`🔍 [DEBUG] Total sources: ${sources.length}, Timeout: ${timeout}ms, Max concurrent: ${maxConcurrent}`);

    // Test sources in batches to avoid overwhelming servers
    for (let i = 0; i < sources.length; i += maxConcurrent) {
      const batch = sources.slice(i, i + maxConcurrent);
      console.log(`🔍 [DEBUG] Testing batch ${Math.floor(i/maxConcurrent) + 1}/${Math.ceil(sources.length/maxConcurrent)}`);

      const batchPromises = batch.map(async (source) => {
        const startTime = Date.now();
        try {
          console.log(`🔍 [DEBUG] Testing: ${source.name || source.id} (${source.provider})`);
          const result = await this.testStream(source.url, timeout);
          const testTime = Date.now() - startTime;

          console.log(`🔍 [DEBUG] Result: ${result.available ? '✅' : '❌'} ${source.name || source.id} (${testTime}ms)`);

          return { source, result };
        } catch (error) {
          const testTime = Date.now() - startTime;
          console.log(`🔍 [DEBUG] Error: ❌ ${source.name || source.id} - ${error.message} (${testTime}ms)`);
          return {
            source,
            result: {
              available: false,
              error: error.message,
              errorType: 'TEST_ERROR'
            }
          };
        }
      });

      const batchResults = await Promise.all(batchPromises);

      batchResults.forEach(({ source, result }) => {
        if (result.available) {
          workingSources.push({ ...source, testResult: result });
          console.log(`✅ [DEBUG] Working source found: ${source.name || source.id}`);
        } else {
          failedSources.push({ ...source, testResult: result });
          console.log(`❌ [DEBUG] Failed source: ${source.name || source.id} - ${result.error || 'Unknown error'}`);
        }
      });
    }

    const successRate = sources.length > 0 ? (workingSources.length / sources.length) * 100 : 0;
    console.log(`📊 [DEBUG] Testing complete: ${workingSources.length}/${sources.length} working (${successRate.toFixed(1)}%)`);

    return {
      working: workingSources,
      failed: failedSources,
      total: sources.length,
      successRate
    };
  },

  // Enhanced method to find the first working source quickly
  findFirstWorkingSource: async (sources, options = {}) => {
    const { timeout = 5000, maxConcurrent = 5 } = options;

    console.log(`🔍 [DEBUG] Starting source testing for ${sources.length} sources`);
    console.log(`🔍 [DEBUG] Timeout: ${timeout}ms, Max concurrent: ${maxConcurrent}`);

    // Test sources in parallel batches
    for (let i = 0; i < sources.length; i += maxConcurrent) {
      const batch = sources.slice(i, i + maxConcurrent);
      console.log(`🔍 [DEBUG] Testing batch ${Math.floor(i/maxConcurrent) + 1}: ${batch.map(s => s.name || s.id).join(', ')}`);

      const batchPromises = batch.map(async (source) => {
        const startTime = Date.now();
        try {
          console.log(`🔍 [DEBUG] Testing source: ${source.name || source.id} - ${source.url}`);
          const result = await this.testStream(source.url, timeout);

          const testTime = Date.now() - startTime;
          console.log(`🔍 [DEBUG] Source ${source.name || source.id}: ${result.available ? '✅ AVAILABLE' : '❌ FAILED'} (${testTime}ms)`);

          if (!result.available) {
            console.log(`🔍 [DEBUG] Source ${source.name || source.id} failed: ${result.error || 'Unknown error'}`);
          }

          if (result.available) {
            return { source, result, success: true };
          }
          return { source, result, success: false };
        } catch (error) {
          const testTime = Date.now() - startTime;
          console.log(`🔍 [DEBUG] Source ${source.name || source.id} error: ${error.message} (${testTime}ms)`);
          return { source, result: { available: false, error: error.message }, success: false };
        }
      });

      const batchResults = await Promise.all(batchPromises);

      // Return the first working source found
      const workingSource = batchResults.find(result => result.success);
      if (workingSource) {
        console.log(`🎉 [DEBUG] Found working source: ${workingSource.source.name || workingSource.source.id}`);
        console.log(`🎉 [DEBUG] URL: ${workingSource.source.url}`);
        console.log(`🎉 [DEBUG] Provider: ${workingSource.source.provider}`);
        return workingSource;
      }
    }

    console.log(`❌ [DEBUG] No working sources found after testing all ${sources.length} sources`);
    return null; // No working sources found
  },

  // Get best available source with automatic testing
  getBestAvailableSource: async (tmdbId, type, season = null, episode = null, options = {}) => {
    const { testSources = true, quality = 'auto' } = options;

    // Get all available sources
    const sources = type === 'movie'
      ? this.getMovieSources(tmdbId, { includeAlternatives: true, quality })
      : this.getTVSources(tmdbId, season, episode, { includeAlternatives: true, quality });

    if (!testSources) {
      return sources[0]; // Return first source without testing
    }

    // Test sources and return the first working one
    const testResult = await this.testMultipleSources(sources);

    if (testResult.working.length > 0) {
      return testResult.working[0];
    }

    // If no sources work, return the primary source with error info
    return {
      ...sources[0],
      testResult: {
        available: false,
        error: 'No working sources found',
        errorType: 'ALL_SOURCES_FAILED'
      }
    };
  },

  // Reset fallback manager
  resetFallbackManager: () => {
    fallbackManager.reset();
    performanceMonitor.startTracking();
  },

  // Get current fallback status
  getFallbackStatus: () => {
    return {
      currentDomain: fallbackManager.getCurrentDomain(),
      retryCount: fallbackManager.retryCount,
      domainLevel: fallbackManager.domainLevel,
      maxRetries: fallbackManager.maxRetries
    };
  },

  // Create secure iframe embed
  createSecureEmbed: (streamData, options = {}) => {
    const {
      width = '100%',
      height = '500',
      autoplay = false,
      muted = false,
      controls = true,
      sandbox = 'allow-same-origin allow-scripts allow-presentation'
    } = options;

    const embedUrl = streamData.url;

    return {
      iframe: `<iframe
        src="${embedUrl}"
        width="${width}"
        height="${height}"
        frameborder="0"
        allowfullscreen
        ${autoplay ? 'autoplay' : ''}
        ${muted ? 'muted' : ''}
        ${controls ? '' : 'controls="false"'}
        sandbox="${sandbox}"
        allow="autoplay; encrypted-media; fullscreen; picture-in-picture"
        referrerpolicy="strict-origin-when-cross-origin"
        loading="lazy"
      ></iframe>`,
      url: embedUrl,
      type: streamData.type,
      provider: streamData.provider,
      security: {
        sandbox,
        referrerPolicy: 'strict-origin-when-cross-origin',
        cspCompliant: true
      }
    };
  }
};

// Helper function to create iframe embed code
export const createEmbedCode = (streamData, options = {}) => {
  const {
    width = '100%',
    height = '500',
    autoplay = false,
    muted = false,
    controls = true
  } = options;

  const embedUrl = streamData.url;

  return {
    iframe: `<iframe
      src="${embedUrl}"
      width="${width}"
      height="${height}"
      frameborder="0"
      allowfullscreen
      ${autoplay ? 'autoplay' : ''}
      ${muted ? 'muted' : ''}
      ${controls ? '' : 'controls="false"'}
      sandbox="allow-same-origin allow-scripts allow-presentation"
    ></iframe>`,
    url: embedUrl,
    type: streamData.type,
    provider: streamData.provider
  };
};

// Error handling for streaming
export const handleStreamError = (error, fallbackSources = []) => {
  console.error('Streaming error:', error);

  if (fallbackSources.length > 0) {
    return {
      success: false,
      error: error.message,
      fallback: fallbackSources[0],
      hasFallback: true
    };
  }

  return {
    success: false,
    error: error.message,
    hasFallback: false
  };
};

